export default ({ src, dest, item }) => {
  return new Promise((resolve, reject) => {
    try {
      // 读取文件mermaid 文件， 通过utf-8编码
      createMermaidPng({ src, dest, item })
      return resolve(item)
    } catch (err) {
      return reject(err)
    }
  })
}
